  top: auto !important;
  bottom: 0;
}

section.story-backdrop button {
  transition: all {{$:/config/AnimationDuration}}ms;
  border: 0 !important;
}

section.story-backdrop > p, section.story-backdrop > p > div.tc-tab-buttons {
  background: rgba(40, 40, 40, 0.3) !important;
}

section.story-backdrop button.tc-tab:not(.tc-tab-selected):hover {
  background-color: <<colour dropdown-tab-background-selected>>;
  filter: contrast(120%);
  border-bottom: 0 !important;
}
