width: 100%;
}

.tc-sidebar-scrollable .tc-sidebar-header > .tc-reveal > .tc-reveal > .tc-sidebar-tabs > p > .tc-sidebar-tabs-main > .tc-tab-buttons {
background: <<colour page-background>>;
position: sticky;
top: 0px;
padding: 14px 10px;
margin: 0;
z-index: 1;
}

<$reveal type="match" state="$:/plugins/Gk0Wk/TiddlySeq/HideSidebarTitle" text="yes">.tc-sidebar-scrollable .tc-sidebar-header .tc-site-title {
display: none;
}</$reveal>

<$reveal type="match" state="$:/plugins/Gk0Wk/TiddlySeq/HideSidebarSubtitle" text="yes">.tc-sidebar-scrollable .tc-sidebar-header .tc-site-subtitle {
display: none;
}</$reveal>

<$reveal type="match" state="$:/plugins/Gk0Wk/TiddlySeq/HideSidebarPageControl" text="yes">.tc-sidebar-scrollable .tc-sidebar-header .tc-page-controls {
display: none;
}</$reveal>

<$reveal type="match" state="$:/plugins/Gk0Wk/TiddlySeq/HideSidebarSearch" text="yes">.tc-sidebar-scrollable .tc-sidebar-header .tc-sidebar-search {
display: none;
}</$reveal>

.tc-sidebar-scrollable .tc-sidebar-header > .tc-reveal > .tc-reveal > .tc-sidebar-tabs > p,
.tc-sidebar-scrollable > div > .tc-sidebar-header > .tc-reveal > div > p {
margin: 0;
}

.tc-sidebar-scrollable > .tc-sidebar-header,
.tc-sidebar-header > .tc-reveal,
.tc-sidebar-header > .tc-reveal > .tc-reveal,
.tc-sidebar-scrollable .tc-tab-set img {
width: 100%;
}

.tc-sidebar-scrollable .tc-tab-set a,
.tc-sidebar-scrollable .tc-tab-set p {
