  color: <<colour foreground>> !important;
  filter: none !important;
}

.tmap-widget .tmap-topbar .tc-drop-down button:hover svg,
.tmap-widget .tmap-topbar .tc-drop-down button:active svg {
  fill: <<colour foreground>> !important;
}

